window.requestAnimFrame=(function(){return window.requestAnimationFrame||window.webkitRequestAnimationFrame||window.mozRequestAnimationFrame||function(a){window.setTimeout(a,1000/60)}})();var canvas=document.getElementById("canvas"),ctx=canvas.getContext("2d"),cw=window.innerWidth,ch=window.innerHeight,fireworks=[],particles=[],hue=120,limiterTotal=5,limiterTick=0,timerTotal=10,timerTick=0,mousedown=false,mx,my;canvas.width=cw;canvas.height=ch;function random(b,a){return Math.random()*(a-b)+b}function calculateDistance(b,a,d,c){var f=b-d,e=a-c;return Math.sqrt(Math.pow(f,2)+Math.pow(e,2))}function Firework(d,c,b,a){this.x=d;this.y=c;this.sx=d;this.sy=c;this.tx=b;this.ty=a;this.distanceToTarget=calculateDistance(d,c,b,a);this.distanceTraveled=0;this.coordinates=[];this.coordinateCount=3;while(this.coordinateCount--){this.coordinates.push([this.x,this.y])}this.angle=Math.atan2(a-c,b-d);this.speed=2;this.acceleration=1.05;this.brightness=random(50,70);this.targetRadius=1}Firework.prototype.update=function(a){this.coordinates.pop();this.coordinates.unshift([this.x,this.y]);if(this.targetRadius<8){this.targetRadius+=0.3}else{this.targetRadius=1}this.speed*=this.acceleration;var c=Math.cos(this.angle)*this.speed,b=Math.sin(this.angle)*this.speed;this.distanceTraveled=calculateDistance(this.sx,this.sy,this.x+c,this.y+b);if(this.distanceTraveled>=this.distanceToTarget){createParticles(this.tx,this.ty);fireworks.splice(a,1)}else{this.x+=c;this.y+=b}};Firework.prototype.draw=function(){ctx.beginPath();ctx.moveTo(this.coordinates[this.coordinates.length-1][0],this.coordinates[this.coordinates.length-1][1]);ctx.lineTo(this.x,this.y);ctx.strokeStyle="hsl("+hue+", 100%, "+this.brightness+"%)";ctx.stroke();ctx.beginPath();ctx.arc(this.tx,this.ty,this.targetRadius,0,Math.PI*2);ctx.stroke()};function Particle(a,b){this.x=a;this.y=b;this.coordinates=[];this.coordinateCount=5;while(this.coordinateCount--){this.coordinates.push([this.x,this.y])}this.angle=random(0,Math.PI*2);this.speed=random(1,10);this.friction=0.95;this.gravity=1;this.hue=random(hue-20,hue+20);this.brightness=random(50,80);this.alpha=1;this.decay=random(0.015,0.03)}Particle.prototype.update=function(a){this.coordinates.pop();this.coordinates.unshift([this.x,this.y]);this.speed*=this.friction;this.x+=Math.cos(this.angle)*this.speed;this.y+=Math.sin(this.angle)*this.speed+this.gravity;this.alpha-=this.decay;if(this.alpha<=this.decay){particles.splice(a,1)}};Particle.prototype.draw=function(){ctx.beginPath();ctx.moveTo(this.coordinates[this.coordinates.length-1][0],this.coordinates[this.coordinates.length-1][1]);ctx.lineTo(this.x,this.y);ctx.strokeStyle="hsla("+this.hue+", 100%, "+this.brightness+"%, "+this.alpha+")";ctx.stroke()};function createParticles(a,c){var b=30;while(b--){particles.push(new Particle(a,c))}}function loop(){requestAnimFrame(loop);hue+=0.5;ctx.globalCompositeOperation="destination-out";ctx.fillStyle="rgba(0, 0, 0, 0.5)";ctx.fillRect(0,0,cw,ch);ctx.globalCompositeOperation="lighter";var a=fireworks.length;while(a--){fireworks[a].draw();fireworks[a].update(a)}var a=particles.length;while(a--){particles[a].draw();particles[a].update(a)}if(timerTick>=timerTotal){if(!mousedown){fireworks.push(new Firework(cw/2,ch,random(0,cw),random(0,ch/2)));timerTick=0}}else{timerTick++}if(limiterTick>=limiterTotal){if(mousedown){fireworks.push(new Firework(cw/2,ch,mx,my));limiterTick=0}}else{limiterTick++}}$(document).ready(function(){loop()});